import ADD from "./ActionName";
import MINUS from "./ActionName";
import REMOVE from "./ActionName";
const add= (item) => {
  return {
    type: ADD,
    payload :{
        id:item.id
    }
  }
};
const minus=(item)=>{
  return {
    type : MINUS,
    payload :{
      id:item.id
    }
  }
}
const remove=(item)=>{
  return { 
    type:REMOVE,
    payload :{
      id:item.id
    }
  }
}
export {add, remove,minus};
