import React, { Component } from "react";
import axios from "axios";
export default class SignIn extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      password: "",
      ms: "no one there",
    };
    this.ChangePw = this.ChangePw.bind(this);
    this.ChangeUn = this.ChangeUn.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }
  ChangeUn(event) {
    this.setState({ username: event.target.value });
  }
  ChangePw(event) {
    this.setState({ password: event.target.value });
  }
  onSubmit() {
    let comfirmPassword = "dasd";
    axios
      .post("http://localhost:5000/users/", {
        username: this.state.username
      })
      .then((res) => comfirmPassword = res.data[0].password
      )
      .catch((err) => console.error(err));
      console.log(comfirmPassword);
    let loginMessage = (comfirmPassword===this.state.password) ? "login  success" : "login failure";
    this.setState({ ms: loginMessage });

  }
  render() {
    return (
      <div >
        <form onSubmit={this.onSubmit}
        >
          <div className="m-2">
            <label for="username" className="form-label">
              Tên Đăng Nhập
            </label>
            <input className="form-control"
              value={this.state.username}
              id="username"
              onChange={this.ChangeUn}
            />
          </div>
          <div class="m-2">
            <label for="password" className="form-label">
              Mật Khẩu
            </label>
            <input className="form-control"
              value={this.state.password}
              onChange={this.ChangePw}
              type="password"
              id="password"
            />
          </div>
          <div class="m-2" >
            <input type="submit" className="m-2" value="Create"></input>
          </div>
        </form>
        <button onClick={this.onSubmit} >test</button>
        <h1>{this.state.username}</h1>
        <br />
        <p>{this.state.password}</p>
        <p>{this.state.ms}</p>
      </div>
    );
  }
}
