import React, { Component } from "react";
import axios from "axios";
export default class SignIn extends Component {
  constructor(props) {
    super(props);
    this.state={
      username:"",
      password:"",
      ms:""
    };
  }
  onchanceusername(event){
    this.setState({username:event.target.value});
  }
  onchancepassword(event){
    this.setState({password:event.target.value});
  }
  onSubmit(){
    axios.get("http://localhost:5000/users/",{username:this.state.username,password:this.state.password}).then(res=>res)
     
  }
  render() {
    return (
      <div className="signin-form">
        <form
          className="bg-light m-4 p-4 border border-secondary border-3"
          style={{ width: "20%", height: "20%", borderRadius: "0.5em" }}
        >
          <div className="mb-3 ">
            <label for="exampleInputEmail1" className="form-label">
              User Name
            </label>
            <input
              type="email"
              className="form-control"
              id="exampleInputEmail1"
          onchange={this.onchangeusername}
            />
           
          </div>
          <div class="mb-3">
            <label for="exampleInputPassword1" class="form-label">
              Password
            </label>
            <input
              type="password"
              className="form-control"
              id="exampleInputPassword1"
            />
          </div>
      
          <button type="submit" className="btn btn-primary">
            Sign In
          </button>
        </form>
      </div>
    );
  }
}
