import React, { Component } from "react";
export default class Item extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <div className="card " style={{ width: "18rem" ,padding:"0.5rem" }}>
        <img
          src={this.props.url}
          className="card-img-top"
          alt={this.props.name}
        />
        <div class="card-body">
          <p class="card-text text-center">{this.props.detail}</p>
          <h3 className="text-center">
            {this.props.cost.toLocaleString("vi", {
              style: "currency",
              currency: "VND",
            })}
          </h3>
          <button className="btn btn-primary btn-block text-center btn-buy" >Mua</button>
        </div>
      </div>
    );
  }
}
