import React from "react";
import { connect } from "react-redux";

import { BiPurchaseTag } from "react-icons/bi";
import { add } from "../redux/Action";
class Presentational extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0,
      quantity:  this.props.NewListItem.find(
        (e) => e.id === this.props.item.id
      ).quantity
    };
    this.Add = this.Add.bind(this);
  }
  Add() {
    this.props.AddToCart(this.props.item);
    this.setState({ count: this.state.count + 1 });
   
  }
  render() {
    return (
      <div>
        {" "}
        <div
          className="card "
          style={{
            width: "13rem",
            height: "17em",
            padding: "0.5rem",
            margin: "0.5rem",
            backgroundColor: "rgb(168, 136, 154)",
          }}
        >
          <img
            style={{ height: "50%", borderRadius: "1em" }}
            src={this.props.item.url}
            className="card-img-top"
            alt={this.props.item.name}
          />
          <div class="card-body">
            <p class="card-text text-center">{this.props.item.name}</p>
            <h3 className="text-center">
              {this.props.item.cost.toLocaleString("en-US", {
                style: "currency",
                currency: "USD",
              })}
            </h3>
            <button
              onClick={this.Add}
              className="btn btn-light btn-block text-center btn-buy mb-3"
            >
              <BiPurchaseTag />
              {
                this.state.quantity
                
              }
            </button>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return { NewListItem: state.itemReducer.OnProcessing };
};

const mapDispatchToProps = (dispatch) => {
  return {
    AddToCart: (item) => {
      dispatch(add(item));
    },
  };
};

const Item = connect(mapStateToProps, mapDispatchToProps)(Presentational);
export default Item;
