import React, { Component } from "react";
import axios from "axios";
export default class SignIn extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      password: "",
      ms: "",
    };
    this.onchancepassword = this.onchancepassword.bind(this);
    this.onchanceusername = this.onchanceusername.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }
  onchanceusername(event) {
    this.setState({ username: event.target.value });
  }
  onchancepassword(event) {
    this.setState({ password: event.target.value });
  }
  onSubmit() {
    axios
      .post("localhost:5000/users/", {
        username: this.state.username
      })
      .then((res) =>

      console.log(res.data)
      )
      .catch((err) => err.json("invalid account"));
  }
  render() {
    return (
      <div >
        <form onSubmit={this.onSubmit}
        >
          <div className="m-2">
            <label for="username" className="form-label">
              Tên Đăng Nhập
            </label>
            <input className="form-control"
              id="username"
              onchange={this.onchangeusername}
            />
          </div>
          <div class="m-2">
            <label for="exampleInputPassword1" className="form-label">
              Mật Khẩu
            </label>
            <input className="form-control"
              type="password"
              id="exampleInputPassword1"
            />
          </div>
          <div class="m-2" >
           <input type="submit" className="m-2" value="Create"></input>
          </div>
        </form>
        <button onClick={this.onSubmit} >test</button>
      </div>
    );
  }
}
