import React, { Component } from "react";
import { Outlet, Link } from "react-router-dom";
import { connect } from "react-redux";

import { TiShoppingCart } from "react-icons/ti";
import { FcHome } from "react-icons/fc";
class NavPresent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      love: "true",
    };
  }

  render() {
    return (
      <div>
        <ul class="nav justify-content-start Navbar">
          <li class="nav-item ">
            <Link className="nav-link link-light" to="/home">
              <FcHome />
              Akanaka
            </Link>
          </li>
          <input type="search" className="searchbar"></input>
          <button className="btn btn-warning">find</button>
          <li class="nav-item l">
            <Link className="nav-link link-light" to="/product">
              Product
            </Link>
          </li>
          <li class="nav-item ">
            <Link
              className={
                "nav-link  link-" +
                (this.props.isChange ? "danger bg-warning" : "light")
              }
              to="/cart"
            >
              <TiShoppingCart />
              {this.props.OnCart.reduce(function (sum, item) {
                return sum + item.quantity;
              }, 0)}
            </Link>
          </li>

          <li class="nav-item ">
            <Link className="nav-link link-light" to="/signup">
              Sign Up
            </Link>
          </li>
        </ul>

        <Outlet />
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    isChange: state.itemReducer.AddProduct,
    OnCart: state.itemReducer.OnProcessing,
  };
};
const Nav = connect(mapStateToProps)(NavPresent);
export default Nav;
