import React from "react";
import { connect } from "react-redux";

import { BiPurchaseTag } from "react-icons/bi";
import { add } from "../redux/Action";
class Presentational extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0,
    };
    this.Add = this.Add.bind(this);
  }
  Add() {
    this.props.AddToCart(this.props.item);
    this.setState({ count: this.state.count + 1 });
  }
  render() {
    return (
      <div>
        <div>
          <img src={this.props.item.url} alt={this.props.item.name} />

          <div>
            <p>{this.props.item.name}</p>
            <h3>
              {this.props.item.cost.toLocaleString("en-US", {
                style: "currency",
                currency: "USD",
              })}
            </h3>
            <div className="cartFooter">
              <button
                onClick={this.Add}
                className="btn btn-secondary  text-center btn-buy mb-3"
              >
                <BiPurchaseTag />
              </button>
              <h4>{this.props.item.quantity}</h4>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return { NewListItem: state.itemReducer.OnProcessing };
};

const mapDispatchToProps = (dispatch) => {
  return {
    AddToCart: (item) => {
      dispatch(add(item));
    },
  };
};

const Item = connect(mapStateToProps, mapDispatchToProps)(Presentational);
export default Item;
