import React, { Component } from "react";
import ListItem from "../Products/product";
import Item from "./item";
export default class Product extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      listProduct: ListItem,
      type: "",
      searchName:"",
    };
    this.changeType = this.changeType.bind(this);
    this.OnchangeSearch=this.OnchangeSearch.bind(this);
  }
  OnchangeSearch(event) {
    this.setState({searchName: event.target.value})
  }
  changeType(event) {
    this.setState({
      type: event.target.value,
    });
  }
  render() {
    const filteredList =
      this.state.type !== ""
        ? this.state.listProduct.filter((item) => item.type === this.state.type)
        : this.state.listProduct;

    return (
      <div className="Shop-Page">
        <div className="Shop-Nav">
          <div className="type-Product"> <label for="type">loại</label>

        <select className="form-select" id="type" onChange={this.changeType}>
          <option value="food">đồ ăn</option>
          <option value="drink">giải khát</option>
          <option value="">tất cả</option>F
        </select></div>
        <input
         type="search"
         value={this.state.searchName}
         onChange={this.OnchangeSearch}
          
        />
        </div>
       <div className="SideBar">
         <div className="sidebar-segment">item 1</div>
         <div className="sidebar-segment">item2</div>
         <div className="sidebar-segment">item 3</div>
         <div className="sidebar-segment">item4</div>
         <div className="sidebar-segment">item5</div>
       </div>
        <div className="Main"> {filteredList.filter(item => {
          
            let name = item.name.toLowerCase();
            
            return name.startsWith(this.state.searchName);
          })
        .map((product) => (
          <Item item={product} />
        ))}</div>

      </div>
    );
  }
}
