import React, { Component } from "react";
import axios from "axios";

export default class Signup extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listuser: [],
    };
  }
  componentDidMount() {
    axios
      .get("http://localhost:5000/users/")
      .then((res) => {
        this.setState({ listuser: res.data });
        console.log(this.state.listuser);
      })
      .catch((err) => {
        console.error(err);
      });
  }
  render() {
    return (
      <div>
  
<form className="bg-light m-4 p-4 border border-secondary border-3"  style={{width: "20%", height: "20%" ,borderRadius:"0.5em"}}>

<div className="mb-3 ">
  <label for="exampleInputEmail1" className="form-label">User Name</label>
  <input type="email" className="form-control" id="exampleInputEmail1" ar   ia-describedby="emailHelp" />
  <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
</div>
<div class="mb-3">
  <label for="exampleInputPassword1" class="form-label">Password</label>
  <input type="password" className="form-control" id="exampleInputPassword1" />
</div>
<div className="mb-3 form-check">
  <input type="checkbox" className="form-check-input" id="exampleCheck1" />
  <label className="form-check-label" for="exampleCheck1">Save</label>
</div>
<button type="submit" className="btn btn-primary">Sign In</button>
</form>
        
      </div>
    );
  }
}
