import React, { Component } from "react";
import {connect} from 'react-redux';
import add from "../redux/Action"
 class Presentational extends React.Component {
  constructor(props) {
    super(props);
    this.state={
      count:0
    }
this.Add=this.Add.bind(this);
  }
  Add(){
    this.props.AddToCart(this.props.id);
    this.setState({count:this.state.count+1});
  }
  render() {
    return (
      <div className="card " style={{ width: "18rem" ,padding:"0.5rem" }}>
        <img
          src={this.props.url}
          className="card-img-top"
          alt={this.props.name}
        />
        <div class="card-body">
          <p class="card-text text-center">{this.props.detail}</p>
          <h3 className="text-center">
            {this.props.cost.toLocaleString("vi", {
              style: "currency",
              currency: "VND",
            })}
          </h3>
          <button onClick={this.Add} className="btn btn-primary btn-block text-center btn-buy" >Mua</button>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {const: state}
};

const mapDispatchToProps = (dispatch) => {
  return {
    AddToCart: (message) => {
      dispatch(add(message))
    }
  }
};

const Item = connect(mapStateToProps, mapDispatchToProps)(Presentational);
export default Item;
