import React, { Component } from 'react';
import { add, minus } from "../redux/Action";
import { connect } from "react-redux";
class Presentational extends Component {
    constructor(props) {
        super(props);
        this.state = {
            count: 0,
        }
        this.Increate = this.Increate.bind(this);
        this.Decreate=this.Decreate.bind(this);
    }
    Increate() {
        this.props.AddItem(this.props.item);
        this.setState({count: this.state.count+1})

    }
    Decreate() {
        this.props.MinusItem(this.props.item);
        
    }
    render() {
        return (
            <div className="bg-success" style={{ width: "30%", height: "30%" }}>
                <button onClick={this.Increate}>+</button>
                <p>{this.props.item.quantity}</p>
                <p>{this.state.count}</ p>
                <button onClick={this.Decreate}>-</button>
                <img className="img-fluid rounded-3 " src={this.props.item.url}></img>
                <h3>{this.props.item.detail}</h3>
                <h3>{this.props.item.price}</h3>
            </div>
        )
    }
}
const mapStateToProps = (state) => {
    return { inCart: state.onProcessing }
}
const mapDispatchToProps = (dispatch) => {
    return {
        AddItem: (item) => { dispatch(add(item)) },
        MinusItem: (item) => { dispatch(minus(item)) }
    }
}

const ItemInCart = connect(mapStateToProps, mapDispatchToProps)(Presentational);
export default ItemInCart;
